INSERT INTO OPENQUERY (PRUEBA322,'select comentario, status, co_cli, fact_num,co_ven,fec_emis, fec_venc, tot_bruto, tot_neto, cond_pago from facturas')
select comentario,status, co_cli, fact_num, co_ven, fec_emis, fec_venc, tot_bruto, tot_neto, cond_pago  from (select f.fact_num, f.co_cli, f.co_ven, fr.status, f.comentario,f.fec_emis, f.fec_venc, f.tot_bruto, f.tot_neto, c.cond_des as cond_pago, ROW_NUMBER() OVER(PARTITION BY f.fact_num order by f.fact_num asc ) rn from ASARTE.dbo.Fact_reng fr, ASARTE.dbo.factura f, ASARTE.dbo.condicio c where fr.fact_num=f.fact_num and f.forma_pag=c.co_cond ) a where rn=1 order by fact_num
